<script>
	import { createEventDispatcher, onMount } from 'svelte';
	import { fade, scale } from 'svelte/transition';
	import { t } from 'svelte-i18n';
	import OverlayScrollbars from 'overlayscrollbars';
	import { playSfx } from '$lib/helpers/audio/audio.svelte';
	import { assets, viewportHeight } from '$lib/store/stores';
	import ButtonModal from './ButtonModal.svelte';

	export let show = false;
	export let title = '';
	export let confirm = true;
	export let confirmText = '';
	export let button = 'all';
	export let sfx = true;
	export let disclaimer = false;
	export let dark = false;
	export let wide = false;

	let content;

	onMount(() =>
		OverlayScrollbars(content, { sizeAutoCapable: false, className: 'os-theme-light' })
	);

	const dispatch = createEventDispatcher();
	const confirmClick = () => {
		dispatch('confirm');
		if (sfx) playSfx();
	};
	const cancelClik = () => {
		dispatch('cancel');
		if (sfx) playSfx();
	};

	const dimiss = () => {
		dispatch('cancel');
	};
</script>

{#if show}
	<div
		class="modal"
		class:dark
		style="height: {$viewportHeight}px;"
		transition:fade={{ duration: 80 }}
		on:click={dimiss}
	>
		<div
			class="modal-content" class:wide on:click|stopPropagation
			transition:scale={{ duration: 80, start: 0.95, opacity: 0.5 }}
		>
			<span class="corner top-left" />
			<span class="corner top-right" />
			<span class="corner bottom-left" />
			<span class="corner bottom-right" />
			<div class="container" style="--bg-icon:url({$assets['modal-bg-icon.png']})">
				{#if title}
					<h1 class="pop-header">{title}</h1>
				{/if}
				<div class="pop-body" class:disclaimer class:large={!title && !confirm} bind:this={content}>
					<slot />
				</div>

				{#if confirm}
					<div class="pop-footer">
						{#if ['cancel', 'all'].indexOf(button) > -1}
							<ButtonModal light={dark} type="cancel" on:click={cancelClik} />
						{/if}
						{#if ['confirm', 'all'].indexOf(button) > -1}
							<ButtonModal light={dark} type="confirm" on:click={confirmClick}>
								{confirmText || $t('confirmButton')}
							</ButtonModal>
						{/if}
					</div>
				{/if}
			</div>
		</div>
	</div>
{/if}

<style>
	.modal {
		position: fixed;
		top: 0;
		left: 0;
		width: 100vw;
		background-color: rgba(0, 0, 0, 0.5);
		z-index: 997;
		display: flex;
		justify-content: center;
		align-items: center;
	}

	.modal-content {
		width: 35rem;
		max-width: 90%;
		background-color: #fbf6ee;
		padding: 8px;
		text-align: center;
		color: #383b40;
		position: relative;
		border-radius: 0.8rem;
	}

	.modal-content.wide {
		width: 50rem;
	}

	.dark .modal-content {
		background-color: #4a5265;
	}

	:global(.mobile) .modal-content {
		max-width: 110vh;
	}

	.container {
		width: 100%;
		height: 100%;
		border: 2px solid #ddd5c8;
		background-image: var(--bg-icon);
		background-repeat: no-repeat;
		background-size: 50%;
		background-position: 50%;
	}

	.corner {
		background-image: url('data:image/webp;base64,');
		background-size: cover;
		width: 2.35rem;
		aspect-ratio: 1/1;
		font-size: 1.5rem;
		display: inline-block;
		position: absolute;
	}

	.top-left {
		top: -0.9rem;
		left: -0.9rem;
		transform: rotate(-90deg);
	}
	.top-right {
		top: -0.9rem;
		right: -0.9rem;
	}
	.bottom-left {
		bottom: -0.9rem;
		left: -0.9rem;
		transform: rotate(-180deg);
	}
	.bottom-right {
		bottom: -0.9rem;
		right: -0.9rem;
		transform: rotate(90deg);
	}

	.pop-header {
		font-size: 130%;
		margin: 0.4rem 7%;
		padding-bottom: 0.4rem;
		border-bottom: 0.2rem solid #ddd5c8;
		position: relative;
		z-index: +1;
	}

	.dark .pop-header {
		color: rgba(210, 198, 156, 1);
	}

	:global(.mobile) .pop-header {
		font-size: 1rem;
	}

	.pop-body {
		height: 15rem;
		max-height: 45vh;
		position: relative;
		z-index: +1;
		font-size: 1.1rem;
		display: flex;
		justify-content: center;
	}

	.dark .pop-body {
		color: #fbf6ee;
	}

	:global(.mobile) .pop-body {
		height: 12rem;
		font-size: 0.9rem;
	}

	.disclaimer {
		height: 18rem;
	}

	.pop-body.large {
		height: 25rem;
		max-height: 70vh;
	}

	.wide .pop-body {
		height: 60vh;
		max-height: unset;
	}

	.pop-footer {
		display: flex;
		padding: 0.7rem 0;
		justify-content: space-around;
		position: relative;
		z-index: +1;
	}
	.pop-footer::before {
		width: 80%;
		content: '';
		display: block;
		border-top: 0.2rem solid #ddd5c8;
		position: absolute;
		top: 0;
		left: 50%;
		transform: translateX(-50%);
	}

	.dark .container,
	.dark .pop-header,
	.dark .pop-footer::before {
		border-color: rgba(210, 198, 156, 0.6);
	}
</style>
